if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/c/Users/super/Documents/SmartClassRoom-Frontend2/node_modules/.pnpm/extract-zip@2.0.1_supports-color@8.1.1/node_modules/extract-zip/node_modules:/mnt/c/Users/super/Documents/SmartClassRoom-Frontend2/node_modules/.pnpm/extract-zip@2.0.1_supports-color@8.1.1/node_modules:/mnt/c/Users/super/Documents/SmartClassRoom-Frontend2/node_modules/.pnpm/node_modules:/mnt/c/Users/super/Documents/SmartClassRoom-Frontend2/node_modules:/mnt/c/Users/super/Documents/node_modules:/mnt/c/Users/super/node_modules:/mnt/c/Users/node_modules:/mnt/c/node_modules:/mnt/c/Users/super/Documents/SmartClassRoom-Frontend2/node_modules/extract-zip/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
